mapper.insert:
INSERT INTO alliance (id, name, leader_id, data, create_time, update_time)
VALUES (#{id}, #{name}, #{leaderId}, #{data}, #{createTime}, #{updateTime})
ON DUPLICATE KEY UPDATE name = #{name}, leader_id = #{leaderId}, data = #{data}, update_time = #{updateTime}

INSERT INTO user_alliance (od_user_id, alliance_id)
VALUES (#{odUserId}, #{allianceId})
ON DUPLICATE KEY UPDATE alliance_id = #{allianceId}
